import React, {useState} from 'react';
import DiwaliItems from './DiwaliItems';

const Diwali = () => {

    const[items, setItems] = useState(DiwaliItems);

    const filterItem = (categItem) => {
        const updatedItems = DiwaliItems.filter((curElem) => {
            return curElem.category === categItem;
        });

        setItems(updatedItems);
    }

    return(
        <>
           <h1 className="text-center main-heading">Diwali Catalogs</h1>
           <hr />

           <div className="diwali-tabs container">
               <div className="menu-tab d-flex justify-content-around">
                   <button className="btn btn-success" onClick={() => filterItem('decoration')}>Decoration</button>
                   <button className="btn btn-success" onClick={() => filterItem('lighting')}>Lighting</button>
                   <button className="btn btn-success">Toran</button>
                   <button className="btn btn-success">Rangoli</button>
                   <button className="btn btn-success">Kandil</button>
                   <button className="btn btn-success">Diya</button>
               </div>
           </div>


           <div className="menu-items container-fluid mt-5">
               <div className="row">
                   <div className="col-11 mx-auto">
                       <div className="row my-5">
                           
                        {
                            items.map((elem) => {
                                const {id, product_name,image, company_name, description, ratings,price} = elem;

                                return(
                                    <div className="">
                                        <div className="row item-inside">

                                            {/*images*/}
                                            <div className="col-6">
                                                <img src={image} alt={product_name} className="img-fluid"/>
                                            </div>

                                            {/*description*/}
                                            <div className="col-6">
                                                <div className="main-title pt-4 pb-3">
                                                    <h4>Product Name:{product_name}</h4>
                                                    <h5>Company Name:{company_name}</h5>
                                                    <p>Description: {description}</p>
                                                </div>

                                                <div className="main-price">
                                                    <div className="price-divide d-flex justify-content-between">
                                                        <h5>Price:{price}</h5>
                                                        <a href="#">
                                                            <button className="btn btn-primary">Add To Cart</button>
                                                        </a>
                                                        <p>Ratings:{ratings}</p>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )
                            })
                        } 
                        </div>
                    </div>
                </div>
            </div>  
        </>
                            
    )
}

export default Diwali
