import React, {useContext,useState} from 'react'
import noteContext from "../../../../context/noteContext"
import './Items.css';

const DiwaliItems = (props) => {

    const { item } = props;

    const context = useContext(noteContext);    
    const {addNote} = context;

    const handleClick = (e)=>{
        e.preventDefault();
        addNote(item.name,item.description,item.price); 
    }

    return (
        
        <div className="main col-md-3 ml-3">
            <div className="card">
                <div className="image-container col-3">
                    <img className="image" src={item.image}></img>
                </div>
                <div className="card-body col-3">
                    <h5 className="card-title">Product:{item.product_name}</h5>
                    <p className="card-text">Description:{item.description}</p>
                    <p className="card-text">Price:{item.price}</p>
                    {/* <i className="fas fa-cart-plus mx-2"></i> */}
                    <button type="submit" className="btn btn-success mx-1" onClick={handleClick} >Add to Cart</button>
                </div>
            </div>
        </div>
        
    
   )
}

export default DiwaliItems
