import React, {useContext,useState} from 'react'
import noteContext from "../../../../context/noteContext"
import './Items.css';

const DiwaliItems = (props) => {

    const { item } = props;

    const context = useContext(noteContext);    
    const {addNote} = context;

    const handleClick = (e)=>{
        e.preventDefault();
        addNote(item.name,item.description,item.price); 
    }

    return (
        <>
        <div className="container">
            <h1>Diwali Catalogs</h1>
            <div className="main col-3.5 ml-4 mr-4 mt-3">
                <div className="image-container col-12">
                    <img className="image" src={item.image}></img>
                </div>
                <div className="card-body col-12">
                    <h5 className="card-title">Product: {item.product_name}</h5>
                    <p className="card-text">Company: {item.company_name}</p>
                    <p className="card-text">Description: {item.description}</p>
                    <p className="card-text">Price: {item.price}</p>
                     
                    <button type="submit" className="btn btn-succes mx-1 cart" onClick={handleClick} ><i className="fas fa-cart-plus mx-2"></i></button>
                    <button className="btn btn-primary mx-1 wishlist"><i class="fas fa-heart"></i></button>
                    <button className="btn btn-primary mx-1 wishlist">Buy Now</button>
                </div>
            </div>
        </div>
        </>
        
    
   )
}

export default DiwaliItems
